Ext.data.JsonP.CKEDITOR_plugins_embedBase_baseDefinition({"tagname":"class","name":"CKEDITOR.plugins.embedBase.baseDefinition","autodetected":{},"files":[{"filename":"plugin.js","href":"plugin30.html#CKEDITOR-plugins-embedBase-baseDefinition"}],"abstract":true,"extends":"CKEDITOR.plugins.widget.definition","members":[{"name":"_cache","tagname":"property","owner":"CKEDITOR.plugins.embedBase.baseDefinition","id":"property-_cache","meta":{"private":true}},{"name":"allowedContent","tagname":"property","owner":"CKEDITOR.feature","id":"property-allowedContent","meta":{}},{"name":"button","tagname":"property","owner":"CKEDITOR.plugins.widget.definition","id":"property-button","meta":{}},{"name":"contentForms","tagname":"property","owner":"CKEDITOR.feature","id":"property-contentForms","meta":{}},{"name":"contentTransformations","tagname":"property","owner":"CKEDITOR.feature","id":"property-contentTransformations","meta":{}},{"name":"data","tagname":"property","owner":"CKEDITOR.plugins.widget.definition","id":"property-data","meta":{}},{"name":"defaults","tagname":"property","owner":"CKEDITOR.plugins.widget.definition","id":"property-defaults","meta":{}},{"name":"dialog","tagname":"property","owner":"CKEDITOR.plugins.widget.definition","id":"property-dialog","meta":{}},{"name":"downcast","tagname":"property","owner":"CKEDITOR.plugins.widget.definition","id":"property-downcast","meta":{}},{"name":"downcasts","tagname":"property","owner":"CKEDITOR.plugins.widget.definition","id":"property-downcasts","meta":{}},{"name":"draggable","tagname":"property","owner":"CKEDITOR.plugins.widget.definition","id":"property-draggable","meta":{}},{"name":"edit","tagname":"property","owner":"CKEDITOR.plugins.widget.definition","id":"property-edit","meta":{}},{"name":"editables","tagname":"property","owner":"CKEDITOR.plugins.widget.definition","id":"property-editables","meta":{}},{"name":"getLabel","tagname":"property","owner":"CKEDITOR.plugins.widget.definition","id":"property-getLabel","meta":{}},{"name":"init","tagname":"property","owner":"CKEDITOR.plugins.widget.definition","id":"property-init","meta":{}},{"name":"inline","tagname":"property","owner":"CKEDITOR.plugins.widget.definition","id":"property-inline","meta":{}},{"name":"insert","tagname":"property","owner":"CKEDITOR.plugins.widget.definition","id":"property-insert","meta":{}},{"name":"mask","tagname":"property","owner":"CKEDITOR.plugins.widget.definition","id":"property-mask","meta":{}},{"name":"name","tagname":"property","owner":"CKEDITOR.plugins.widget.definition","id":"property-name","meta":{}},{"name":"parts","tagname":"property","owner":"CKEDITOR.plugins.widget.definition","id":"property-parts","meta":{}},{"name":"pathName","tagname":"property","owner":"CKEDITOR.plugins.widget.definition","id":"property-pathName","meta":{}},{"name":"providerUrl","tagname":"property","owner":"CKEDITOR.plugins.embedBase.baseDefinition","id":"property-providerUrl","meta":{}},{"name":"requiredContent","tagname":"property","owner":"CKEDITOR.feature","id":"property-requiredContent","meta":{}},{"name":"styleToAllowedContentRules","tagname":"property","owner":"CKEDITOR.plugins.widget.definition","id":"property-styleToAllowedContentRules","meta":{}},{"name":"styleableElements","tagname":"property","owner":"CKEDITOR.plugins.widget.definition","id":"property-styleableElements","meta":{}},{"name":"template","tagname":"property","owner":"CKEDITOR.plugins.widget.definition","id":"property-template","meta":{}},{"name":"upcast","tagname":"property","owner":"CKEDITOR.plugins.widget.definition","id":"property-upcast","meta":{}},{"name":"upcastPriority","tagname":"property","owner":"CKEDITOR.plugins.widget.definition","id":"property-upcastPriority","meta":{}},{"name":"upcasts","tagname":"property","owner":"CKEDITOR.plugins.widget.definition","id":"property-upcasts","meta":{}},{"name":"urlRegExp","tagname":"property","owner":"CKEDITOR.plugins.embedBase.baseDefinition","id":"property-urlRegExp","meta":{}},{"name":"_cacheResponse","tagname":"method","owner":"CKEDITOR.plugins.embedBase.baseDefinition","id":"method-_cacheResponse","meta":{"private":true}},{"name":"_createTask","tagname":"method","owner":"CKEDITOR.plugins.embedBase.baseDefinition","id":"method-_createTask","meta":{"private":true}},{"name":"_getCachedResponse","tagname":"method","owner":"CKEDITOR.plugins.embedBase.baseDefinition","id":"method-_getCachedResponse","meta":{"private":true}},{"name":"_handleError","tagname":"method","owner":"CKEDITOR.plugins.embedBase.baseDefinition","id":"method-_handleError","meta":{"private":true}},{"name":"_handleResponse","tagname":"method","owner":"CKEDITOR.plugins.embedBase.baseDefinition","id":"method-_handleResponse","meta":{"private":true}},{"name":"_responseToHtml","tagname":"method","owner":"CKEDITOR.plugins.embedBase.baseDefinition","id":"method-_responseToHtml","meta":{"private":true}},{"name":"_sendRequest","tagname":"method","owner":"CKEDITOR.plugins.embedBase.baseDefinition","id":"method-_sendRequest","meta":{"private":true}},{"name":"_setContent","tagname":"method","owner":"CKEDITOR.plugins.embedBase.baseDefinition","id":"method-_setContent","meta":{"private":true}},{"name":"getErrorMessage","tagname":"method","owner":"CKEDITOR.plugins.embedBase.baseDefinition","id":"method-getErrorMessage","meta":{}},{"name":"isUrlValid","tagname":"method","owner":"CKEDITOR.plugins.embedBase.baseDefinition","id":"method-isUrlValid","meta":{}},{"name":"loadContent","tagname":"method","owner":"CKEDITOR.plugins.embedBase.baseDefinition","id":"method-loadContent","meta":{}},{"name":"toFeature","tagname":"method","owner":"CKEDITOR.feature","id":"method-toFeature","meta":{}},{"name":"handleResponse","tagname":"event","owner":"CKEDITOR.plugins.embedBase.baseDefinition","id":"event-handleResponse","meta":{}},{"name":"sendRequest","tagname":"event","owner":"CKEDITOR.plugins.embedBase.baseDefinition","id":"event-sendRequest","meta":{}},{"name":"validateUrl","tagname":"event","owner":"CKEDITOR.plugins.embedBase.baseDefinition","id":"event-validateUrl","meta":{}}],"alternateClassNames":[],"aliases":{},"id":"class-CKEDITOR.plugins.embedBase.baseDefinition","short_doc":"An embed widget base definition. ...","component":false,"superclasses":["CKEDITOR.plugins.widget.definition"],"subclasses":[],"mixedInto":[],"mixins":[],"parentMixins":["CKEDITOR.feature"],"requires":[],"uses":[],"html":"<div><pre class=\"hierarchy\"><h4>Hierarchy</h4><div class='subclass first-child'><a href='#!/api/CKEDITOR.plugins.widget.definition' rel='CKEDITOR.plugins.widget.definition' class='docClass'>CKEDITOR.plugins.widget.definition</a><div class='subclass '><strong>CKEDITOR.plugins.embedBase.baseDefinition</strong></div></div><h4>Inherited mixins</h4><div class='dependency'><a href='#!/api/CKEDITOR.feature' rel='CKEDITOR.feature' class='docClass'>CKEDITOR.feature</a></div><h4>Files</h4><div class='dependency'><a href='source/plugin30.html#CKEDITOR-plugins-embedBase-baseDefinition' target='_blank'>plugin.js</a></div></pre><div class='doc-contents'><p>An embed widget base definition. It predefines a few <a href=\"#!/api/CKEDITOR.plugins.widget.definition\" rel=\"CKEDITOR.plugins.widget.definition\" class=\"docClass\">widget definition</a>\nproperties such as <a href=\"#!/api/CKEDITOR.plugins.embedBase.baseDefinition-property-mask\" rel=\"CKEDITOR.plugins.embedBase.baseDefinition-property-mask\" class=\"docClass\">mask</a>, <a href=\"#!/api/CKEDITOR.plugins.embedBase.baseDefinition-property-template\" rel=\"CKEDITOR.plugins.embedBase.baseDefinition-property-template\" class=\"docClass\">template</a> and <a href=\"#!/api/CKEDITOR.plugins.embedBase.baseDefinition-property-pathName\" rel=\"CKEDITOR.plugins.embedBase.baseDefinition-property-pathName\" class=\"docClass\">pathName</a> and adds methods related to\ncontent embedding.</p>\n\n<p>To create a base definition use the <a href=\"#!/api/CKEDITOR.plugins.embedBase-static-method-createWidgetBaseDefinition\" rel=\"CKEDITOR.plugins.embedBase-static-method-createWidgetBaseDefinition\" class=\"docClass\">CKEDITOR.plugins.embedBase.createWidgetBaseDefinition</a> method.</p>\n\n<p>Note: For easier browsing of this class's API you can hide inherited method using the \"Show\" drop-down\non the right-hand side.</p>\n</div><div class='members'><div class='members-section'><div class='definedBy'>Defined By</div><h3 class='members-title icon-property'>Properties</h3><div class='subsection'><div id='property-_cache' class='member first-child not-inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><span class='defined-in' rel='CKEDITOR.plugins.embedBase.baseDefinition'>CKEDITOR.plugins.embedBase.baseDefinition</span><br/><a href='source/plugin30.html#CKEDITOR-plugins-embedBase-baseDefinition-property-_cache' target='_blank' class='view-source'>view source</a></div><a href='#!/api/CKEDITOR.plugins.embedBase.baseDefinition-property-_cache' class='name expandable'>_cache</a> : Object<span class=\"signature\"><span class='private' >private</span></span></div><div class='description'><div class='short'>Response cache. ...</div><div class='long'><p>Response cache. This cache object will be shared between all instances of this widget.</p>\n<p>Defaults to: <code>{}</code></p></div></div></div><div id='property-allowedContent' class='member  inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><a href='#!/api/CKEDITOR.feature' rel='CKEDITOR.feature' class='defined-in docClass'>CKEDITOR.feature</a><br/><a href='source/filter.html#CKEDITOR-feature-property-allowedContent' target='_blank' class='view-source'>view source</a></div><a href='#!/api/CKEDITOR.feature-property-allowedContent' class='name expandable'>allowedContent</a> : <a href=\"#!/api/CKEDITOR.filter.allowedContentRules\" rel=\"CKEDITOR.filter.allowedContentRules\" class=\"docClass\">CKEDITOR.filter.allowedContentRules</a><span class=\"signature\"></span></div><div class='description'><div class='short'>HTML code that can be generated by this feature. ...</div><div class='long'><p>HTML code that can be generated by this feature.</p>\n\n<p>For example a basic image feature (image button displaying the image dialog window)\nmay allow <code>'img[!src,alt,width,height]'</code>.</p>\n\n<p>During the feature activation this value is passed to <a href=\"#!/api/CKEDITOR.filter-method-allow\" rel=\"CKEDITOR.filter-method-allow\" class=\"docClass\">CKEDITOR.filter.allow</a>.</p>\n<p>Defaults to: <code>null</code></p></div></div></div><div id='property-button' class='member  inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><a href='#!/api/CKEDITOR.plugins.widget.definition' rel='CKEDITOR.plugins.widget.definition' class='defined-in docClass'>CKEDITOR.plugins.widget.definition</a><br/><a href='source/plugin103.html#CKEDITOR-plugins-widget-definition-property-button' target='_blank' class='view-source'>view source</a></div><a href='#!/api/CKEDITOR.plugins.widget.definition-property-button' class='name expandable'>button</a> : String<span class=\"signature\"></span></div><div class='description'><div class='short'>The label for the widget toolbar button. ...</div><div class='long'><p>The label for the widget toolbar button.</p>\n\n<pre><code>editor.widgets.add( 'simplebox', {\n    button: 'Create a simple box'\n} );\n\neditor.widgets.add( 'simplebox', {\n    button: editor.lang.simplebox.title\n} );\n</code></pre>\n</div></div></div><div id='property-contentForms' class='member  inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><a href='#!/api/CKEDITOR.feature' rel='CKEDITOR.feature' class='defined-in docClass'>CKEDITOR.feature</a><br/><a href='source/filter.html#CKEDITOR-feature-property-contentForms' target='_blank' class='view-source'>view source</a></div><a href='#!/api/CKEDITOR.feature-property-contentForms' class='name expandable'>contentForms</a> : Object<span class=\"signature\"></span></div><div class='description'><div class='short'>Feature content forms to be registered in the CKEDITOR.editor.filter\nduring the feature activation. ...</div><div class='long'><p>Feature content forms to be registered in the <a href=\"#!/api/CKEDITOR.editor-property-filter\" rel=\"CKEDITOR.editor-property-filter\" class=\"docClass\">CKEDITOR.editor.filter</a>\nduring the feature activation.</p>\n\n<p>See <a href=\"#!/api/CKEDITOR.filter-method-addContentForms\" rel=\"CKEDITOR.filter-method-addContentForms\" class=\"docClass\">CKEDITOR.filter.addContentForms</a> for more details.</p>\n<p>Defaults to: <code>null</code></p></div></div></div><div id='property-contentTransformations' class='member  inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><a href='#!/api/CKEDITOR.feature' rel='CKEDITOR.feature' class='defined-in docClass'>CKEDITOR.feature</a><br/><a href='source/filter.html#CKEDITOR-feature-property-contentTransformations' target='_blank' class='view-source'>view source</a></div><a href='#!/api/CKEDITOR.feature-property-contentTransformations' class='name expandable'>contentTransformations</a> : Object<span class=\"signature\"></span></div><div class='description'><div class='short'>Transformations (usually for content generated by this feature, but not necessarily)\nthat will be registered in the C...</div><div class='long'><p>Transformations (usually for content generated by this feature, but not necessarily)\nthat will be registered in the <a href=\"#!/api/CKEDITOR.editor-property-filter\" rel=\"CKEDITOR.editor-property-filter\" class=\"docClass\">CKEDITOR.editor.filter</a> during the feature activation.</p>\n\n<p>See <a href=\"#!/api/CKEDITOR.filter-method-addTransformations\" rel=\"CKEDITOR.filter-method-addTransformations\" class=\"docClass\">CKEDITOR.filter.addTransformations</a> for more details.</p>\n<p>Defaults to: <code>null</code></p></div></div></div><div id='property-data' class='member  inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><a href='#!/api/CKEDITOR.plugins.widget.definition' rel='CKEDITOR.plugins.widget.definition' class='defined-in docClass'>CKEDITOR.plugins.widget.definition</a><br/><a href='source/plugin103.html#CKEDITOR-plugins-widget-definition-property-data' target='_blank' class='view-source'>view source</a></div><a href='#!/api/CKEDITOR.plugins.widget.definition-property-data' class='name expandable'>data</a> : Function<span class=\"signature\"></span></div><div class='description'><div class='short'>If set, it will be added as the CKEDITOR.plugins.widget.data event listener. ...</div><div class='long'><p>If set, it will be added as the <a href=\"#!/api/CKEDITOR.plugins.widget-event-data\" rel=\"CKEDITOR.plugins.widget-event-data\" class=\"docClass\">CKEDITOR.plugins.widget.data</a> event listener.\nThis means that it will be executed every time the <a href=\"#!/api/CKEDITOR.plugins.widget-property-data\" rel=\"CKEDITOR.plugins.widget-property-data\" class=\"docClass\">widget data</a> changes.</p>\n</div></div></div><div id='property-defaults' class='member  inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><a href='#!/api/CKEDITOR.plugins.widget.definition' rel='CKEDITOR.plugins.widget.definition' class='defined-in docClass'>CKEDITOR.plugins.widget.definition</a><br/><a href='source/plugin103.html#CKEDITOR-plugins-widget-definition-property-defaults' target='_blank' class='view-source'>view source</a></div><a href='#!/api/CKEDITOR.plugins.widget.definition-property-defaults' class='name expandable'>defaults</a> : Object<span class=\"signature\"></span></div><div class='description'><div class='short'>The data object which will be used to populate the data of a newly created widget. ...</div><div class='long'><p>The data object which will be used to populate the data of a newly created widget.\nSee <a href=\"#!/api/CKEDITOR.plugins.widget-property-data\" rel=\"CKEDITOR.plugins.widget-property-data\" class=\"docClass\">CKEDITOR.plugins.widget.data</a>.</p>\n\n<pre><code>defaults: {\n    showCaption: true,\n    align: 'none'\n}\n</code></pre>\n</div></div></div><div id='property-dialog' class='member  inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><a href='#!/api/CKEDITOR.plugins.widget.definition' rel='CKEDITOR.plugins.widget.definition' class='defined-in docClass'>CKEDITOR.plugins.widget.definition</a><br/><a href='source/plugin103.html#CKEDITOR-plugins-widget-definition-property-dialog' target='_blank' class='view-source'>view source</a></div><a href='#!/api/CKEDITOR.plugins.widget.definition-property-dialog' class='name expandable'>dialog</a> : String<span class=\"signature\"></span></div><div class='description'><div class='short'>The name of a dialog window which will be opened on CKEDITOR.plugins.widget.edit. ...</div><div class='long'><p>The name of a dialog window which will be opened on <a href=\"#!/api/CKEDITOR.plugins.widget-method-edit\" rel=\"CKEDITOR.plugins.widget-method-edit\" class=\"docClass\">CKEDITOR.plugins.widget.edit</a>.\nIf not defined, then the <a href=\"#!/api/CKEDITOR.plugins.widget-method-edit\" rel=\"CKEDITOR.plugins.widget-method-edit\" class=\"docClass\">CKEDITOR.plugins.widget.edit</a> method will not perform any action and\nwidget's command will insert a new widget without opening a dialog window first.</p>\n</div></div></div><div id='property-downcast' class='member  inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><a href='#!/api/CKEDITOR.plugins.widget.definition' rel='CKEDITOR.plugins.widget.definition' class='defined-in docClass'>CKEDITOR.plugins.widget.definition</a><br/><a href='source/plugin103.html#CKEDITOR-plugins-widget-definition-property-downcast' target='_blank' class='view-source'>view source</a></div><a href='#!/api/CKEDITOR.plugins.widget.definition-property-downcast' class='name expandable'>downcast</a> : String/Function<span class=\"signature\"></span></div><div class='description'><div class='short'>The function to be used to downcast this widget or\na name of the downcast option from the downcasts object. ...</div><div class='long'><p>The function to be used to downcast this widget or\na name of the downcast option from the <a href=\"#!/api/CKEDITOR.plugins.widget.definition-property-downcasts\" rel=\"CKEDITOR.plugins.widget.definition-property-downcasts\" class=\"docClass\">downcasts</a> object.</p>\n\n<p>The downcast funciton will be executed in the <a href=\"#!/api/CKEDITOR.plugins.widget\" rel=\"CKEDITOR.plugins.widget\" class=\"docClass\">CKEDITOR.plugins.widget</a> context\nand with <code>widgetElement</code> (<a href=\"#!/api/CKEDITOR.htmlParser.element\" rel=\"CKEDITOR.htmlParser.element\" class=\"docClass\">CKEDITOR.htmlParser.element</a>) argument which is\nthe widget's main element.</p>\n\n<p>The function may return an instance of the <a href=\"#!/api/CKEDITOR.htmlParser.node\" rel=\"CKEDITOR.htmlParser.node\" class=\"docClass\">CKEDITOR.htmlParser.node</a> class if the widget\nneeds to be downcasted to a different node than the widget's main element.</p>\n</div></div></div><div id='property-downcasts' class='member  inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><a href='#!/api/CKEDITOR.plugins.widget.definition' rel='CKEDITOR.plugins.widget.definition' class='defined-in docClass'>CKEDITOR.plugins.widget.definition</a><br/><a href='source/plugin103.html#CKEDITOR-plugins-widget-definition-property-downcasts' target='_blank' class='view-source'>view source</a></div><a href='#!/api/CKEDITOR.plugins.widget.definition-property-downcasts' class='name expandable'>downcasts</a> : Object<span class=\"signature\"></span></div><div class='description'><div class='short'>The object containing functions which can be used to downcast this widget. ...</div><div class='long'><p>The object containing functions which can be used to downcast this widget.\nOnly the one pointed by the <a href=\"#!/api/CKEDITOR.plugins.widget.definition-property-downcast\" rel=\"CKEDITOR.plugins.widget.definition-property-downcast\" class=\"docClass\">downcast</a> property will be used.</p>\n\n<p>In most cases it is appropriate to use <a href=\"#!/api/CKEDITOR.plugins.widget.definition-property-downcast\" rel=\"CKEDITOR.plugins.widget.definition-property-downcast\" class=\"docClass\">downcast</a> directly,\nbecause majority of widgets have just one variant of downcasting (or none at all).\nHowever, in some cases the widget author may want to expose more than one variant\nand then this property may be used.</p>\n\n<pre><code>downcasts: {\n    // This downcast may transform the widget into the figure element.\n    figure: function() {\n        // ...\n    },\n    // This downcast may transform the widget into the image element with data-* attributes.\n    image: function() {\n        // ...\n    }\n}\n\n// Then, the widget user may choose one of the downcast options when setting up his editor.\neditor.on( 'widgetDefinition', function( evt ) {\n    if ( evt.data.name == 'image' )\n            evt.data.downcast = 'figure';\n} );\n</code></pre>\n</div></div></div><div id='property-draggable' class='member  inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><a href='#!/api/CKEDITOR.plugins.widget.definition' rel='CKEDITOR.plugins.widget.definition' class='defined-in docClass'>CKEDITOR.plugins.widget.definition</a><br/><a href='source/plugin103.html#CKEDITOR-plugins-widget-definition-property-draggable' target='_blank' class='view-source'>view source</a></div><a href='#!/api/CKEDITOR.plugins.widget.definition-property-draggable' class='name expandable'>draggable</a> : Boolean<span class=\"signature\"></span></div><div class='description'><div class='short'>Whether widget should be draggable. ...</div><div class='long'><p>Whether widget should be draggable. Defaults to <code>true</code>.\nIf set to <code>false</code> drag handler will not be displayed when hovering widget.</p>\n</div></div></div><div id='property-edit' class='member  inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><a href='#!/api/CKEDITOR.plugins.widget.definition' rel='CKEDITOR.plugins.widget.definition' class='defined-in docClass'>CKEDITOR.plugins.widget.definition</a><br/><a href='source/plugin103.html#CKEDITOR-plugins-widget-definition-property-edit' target='_blank' class='view-source'>view source</a></div><a href='#!/api/CKEDITOR.plugins.widget.definition-property-edit' class='name expandable'>edit</a> : Function<span class=\"signature\"></span></div><div class='description'><div class='short'>If set, it will be added as the CKEDITOR.plugins.widget.edit event listener. ...</div><div class='long'><p>If set, it will be added as the <a href=\"#!/api/CKEDITOR.plugins.widget-event-edit\" rel=\"CKEDITOR.plugins.widget-event-edit\" class=\"docClass\">CKEDITOR.plugins.widget.edit</a> event listener.\nThis means that it will be executed when a widget is being edited.\nSee the <a href=\"#!/api/CKEDITOR.plugins.widget-method-edit\" rel=\"CKEDITOR.plugins.widget-method-edit\" class=\"docClass\">CKEDITOR.plugins.widget.edit</a> method.</p>\n</div></div></div><div id='property-editables' class='member  inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><a href='#!/api/CKEDITOR.plugins.widget.definition' rel='CKEDITOR.plugins.widget.definition' class='defined-in docClass'>CKEDITOR.plugins.widget.definition</a><br/><a href='source/plugin103.html#CKEDITOR-plugins-widget-definition-property-editables' target='_blank' class='view-source'>view source</a></div><a href='#!/api/CKEDITOR.plugins.widget.definition-property-editables' class='name expandable'>editables</a> : Object<span class=\"signature\"></span></div><div class='description'><div class='short'>An object containing definitions of nested editables (editable name => CKEDITOR.plugins.widget.nestedEditable.definit...</div><div class='long'><p>An object containing definitions of nested editables (editable name => <a href=\"#!/api/CKEDITOR.plugins.widget.nestedEditable.definition\" rel=\"CKEDITOR.plugins.widget.nestedEditable.definition\" class=\"docClass\">CKEDITOR.plugins.widget.nestedEditable.definition</a>).\nNote that editables <em>have to</em> be defined in the same order as they are in DOM / <a href=\"#!/api/CKEDITOR.plugins.widget.definition-property-template\" rel=\"CKEDITOR.plugins.widget.definition-property-template\" class=\"docClass\">template</a>.\nOtherwise errors will occur when nesting widgets inside each other.</p>\n\n<pre><code>editables: {\n    header: 'h1',\n    content: {\n        selector: 'div.content',\n        allowedContent: 'p strong em; a[!href]'\n    }\n}\n</code></pre>\n</div></div></div><div id='property-getLabel' class='member  inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><a href='#!/api/CKEDITOR.plugins.widget.definition' rel='CKEDITOR.plugins.widget.definition' class='defined-in docClass'>CKEDITOR.plugins.widget.definition</a><br/><a href='source/plugin103.html#CKEDITOR-plugins-widget-definition-property-getLabel' target='_blank' class='view-source'>view source</a></div><a href='#!/api/CKEDITOR.plugins.widget.definition-property-getLabel' class='name expandable'>getLabel</a> : Function<span class=\"signature\"></span></div><div class='description'><div class='short'>The function used to obtain an accessibility label for the widget. ...</div><div class='long'><p>The function used to obtain an accessibility label for the widget. It might be used to make\nthe widget labels as precise as possible, since it has access to the widget instance.</p>\n\n<p>If not specified, the default implementation will use the <a href=\"#!/api/CKEDITOR.plugins.widget.definition-property-pathName\" rel=\"CKEDITOR.plugins.widget.definition-property-pathName\" class=\"docClass\">pathName</a> or the main\n<a href=\"#!/api/CKEDITOR.plugins.widget-property-element\" rel=\"CKEDITOR.plugins.widget-property-element\" class=\"docClass\">element</a> tag name.</p>\n</div></div></div><div id='property-init' class='member  inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><a href='#!/api/CKEDITOR.plugins.widget.definition' rel='CKEDITOR.plugins.widget.definition' class='defined-in docClass'>CKEDITOR.plugins.widget.definition</a><br/><a href='source/plugin103.html#CKEDITOR-plugins-widget-definition-property-init' target='_blank' class='view-source'>view source</a></div><a href='#!/api/CKEDITOR.plugins.widget.definition-property-init' class='name expandable'>init</a> : Function<span class=\"signature\"></span></div><div class='description'><div class='short'>The method executed while initializing a widget, after a widget instance\nis created, but before it is ready. ...</div><div class='long'><p>The method executed while initializing a widget, after a widget instance\nis created, but before it is ready. It is executed before the first\n<a href=\"#!/api/CKEDITOR.plugins.widget-event-data\" rel=\"CKEDITOR.plugins.widget-event-data\" class=\"docClass\">CKEDITOR.plugins.widget.data</a> is fired so it is common to\nuse the <code>init</code> method to populate widget data with information loaded from\nthe DOM, like for exmaple:</p>\n\n<pre><code>init: function() {\n    this.setData( 'width', this.element.getStyle( 'width' ) );\n\n    if ( this.parts.caption.getStyle( 'display' ) != 'none' )\n        this.setData( 'showCaption', true );\n}\n</code></pre>\n</div></div></div><div id='property-inline' class='member  inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><a href='#!/api/CKEDITOR.plugins.widget.definition' rel='CKEDITOR.plugins.widget.definition' class='defined-in docClass'>CKEDITOR.plugins.widget.definition</a><br/><a href='source/plugin103.html#CKEDITOR-plugins-widget-definition-property-inline' target='_blank' class='view-source'>view source</a></div><a href='#!/api/CKEDITOR.plugins.widget.definition-property-inline' class='name expandable'>inline</a> : Boolean<span class=\"signature\"></span></div><div class='description'><div class='short'>If set to true/false, it will force the widget to be either an inline or a block widget. ...</div><div class='long'><p>If set to <code>true/false</code>, it will force the widget to be either an inline or a block widget.\nIf not set, the widget type will be determined from the widget element.</p>\n\n<p>Widget type influences whether a block (<code>div</code>) or an inline (<code>span</code>) element is used\nfor the wrapper.</p>\n</div></div></div><div id='property-insert' class='member  inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><a href='#!/api/CKEDITOR.plugins.widget.definition' rel='CKEDITOR.plugins.widget.definition' class='defined-in docClass'>CKEDITOR.plugins.widget.definition</a><br/><a href='source/plugin103.html#CKEDITOR-plugins-widget-definition-property-insert' target='_blank' class='view-source'>view source</a></div><a href='#!/api/CKEDITOR.plugins.widget.definition-property-insert' class='name expandable'>insert</a> : Function<span class=\"signature\"></span></div><div class='description'><div class='short'>The method to be executed when the widget's command is executed in order to insert a new widget\n(widget of this type ...</div><div class='long'><p>The method to be executed when the widget's command is executed in order to insert a new widget\n(widget of this type is not focused). If not defined, then the default action will be\nperformed which means that:</p>\n\n<ul>\n<li>An instance of the widget will be created in a detached <a href=\"#!/api/CKEDITOR.dom.documentFragment\" rel=\"CKEDITOR.dom.documentFragment\" class=\"docClass\">document fragment</a>,</li>\n<li>The <a href=\"#!/api/CKEDITOR.plugins.widget-method-edit\" rel=\"CKEDITOR.plugins.widget-method-edit\" class=\"docClass\">CKEDITOR.plugins.widget.edit</a> method will be called to trigger widget editing,</li>\n<li>The widget element will be inserted into DOM.</li>\n</ul>\n\n</div></div></div><div id='property-mask' class='member  inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><a href='#!/api/CKEDITOR.plugins.widget.definition' rel='CKEDITOR.plugins.widget.definition' class='defined-in docClass'>CKEDITOR.plugins.widget.definition</a><br/><a href='source/plugin103.html#CKEDITOR-plugins-widget-definition-property-mask' target='_blank' class='view-source'>view source</a></div><a href='#!/api/CKEDITOR.plugins.widget.definition-property-mask' class='name expandable'>mask</a> : Boolean<span class=\"signature\"></span></div><div class='description'><div class='short'>If set to true, the widget's element will be covered with a transparent mask. ...</div><div class='long'><p>If set to <code>true</code>, the widget's element will be covered with a transparent mask.\nThis will prevent its content from being clickable, which matters in case\nof special elements like embedded Flash or iframes that generate a separate \"context\".</p>\n</div></div></div><div id='property-name' class='member  inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><a href='#!/api/CKEDITOR.plugins.widget.definition' rel='CKEDITOR.plugins.widget.definition' class='defined-in docClass'>CKEDITOR.plugins.widget.definition</a><br/><a href='source/plugin103.html#CKEDITOR-plugins-widget-definition-property-name' target='_blank' class='view-source'>view source</a></div><a href='#!/api/CKEDITOR.plugins.widget.definition-property-name' class='name expandable'>name</a> : String<span class=\"signature\"></span></div><div class='description'><div class='short'>Widget definition name. ...</div><div class='long'><p>Widget definition name. It is automatically set when the definition is\n<a href=\"#!/api/CKEDITOR.plugins.widget.repository-method-add\" rel=\"CKEDITOR.plugins.widget.repository-method-add\" class=\"docClass\">registered</a>.</p>\n<p>Overrides: <a href=\"#!/api/CKEDITOR.feature-property-name\" rel=\"CKEDITOR.feature-property-name\" class=\"docClass\">CKEDITOR.feature.name</a></p></div></div></div><div id='property-parts' class='member  inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><a href='#!/api/CKEDITOR.plugins.widget.definition' rel='CKEDITOR.plugins.widget.definition' class='defined-in docClass'>CKEDITOR.plugins.widget.definition</a><br/><a href='source/plugin103.html#CKEDITOR-plugins-widget-definition-property-parts' target='_blank' class='view-source'>view source</a></div><a href='#!/api/CKEDITOR.plugins.widget.definition-property-parts' class='name expandable'>parts</a> : Object<span class=\"signature\"></span></div><div class='description'><div class='short'>An object containing definitions of widget components (part name => CSS selector). ...</div><div class='long'><p>An object containing definitions of widget components (part name => CSS selector).</p>\n\n<pre><code>parts: {\n    image: 'img',\n    caption: 'div.caption'\n}\n</code></pre>\n</div></div></div><div id='property-pathName' class='member  inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><a href='#!/api/CKEDITOR.plugins.widget.definition' rel='CKEDITOR.plugins.widget.definition' class='defined-in docClass'>CKEDITOR.plugins.widget.definition</a><br/><a href='source/plugin103.html#CKEDITOR-plugins-widget-definition-property-pathName' target='_blank' class='view-source'>view source</a></div><a href='#!/api/CKEDITOR.plugins.widget.definition-property-pathName' class='name expandable'>pathName</a> : String<span class=\"signature\"></span></div><div class='description'><div class='short'><p>The widget name displayed in the elements path.</p>\n</div><div class='long'><p>The widget name displayed in the elements path.</p>\n</div></div></div><div id='property-providerUrl' class='member  not-inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><span class='defined-in' rel='CKEDITOR.plugins.embedBase.baseDefinition'>CKEDITOR.plugins.embedBase.baseDefinition</span><br/><a href='source/plugin30.html#CKEDITOR-plugins-embedBase-baseDefinition-property-providerUrl' target='_blank' class='view-source'>view source</a></div><a href='#!/api/CKEDITOR.plugins.embedBase.baseDefinition-property-providerUrl' class='name expandable'>providerUrl</a> : <a href=\"#!/api/CKEDITOR.template\" rel=\"CKEDITOR.template\" class=\"docClass\">CKEDITOR.template</a><span class=\"signature\"></span></div><div class='description'><div class='short'>The template used to generate the URL of the content provider. ...</div><div class='long'><p>The template used to generate the URL of the content provider. Content provider is a service\nwhich the embed widget will request in order to get an <a href=\"http://www.oembed.com/\">oEmbed</a> response that\ncan be transformed into content which can be embedded in the editor.</p>\n\n<p>Example content providers are:</p>\n\n<ul>\n<li><a href=\"https://iframely.com/\">Iframely</a>,</li>\n<li><a href=\"https://noembed.com/\">Noembed</a>.</li>\n</ul>\n\n\n<p>Both Iframely and Noembed are <strong>proxy</strong> services which support <strong>JSONP requests</strong>, hence they are not limited by the\nsame-origin policy. Unfortunately, usually oEmbed services exposed by real content providers\nlike YouTube or Twitter do not support XHR with CORS or do not support oEmbed at all which makes it\nimpossible or hard to get such content to be embedded in the editor. This problem is solved by proxy content providers\nlike Iframely and Noembed.</p>\n\n<p>This property must be defined after creating an embed widget base definition.</p>\n\n<p>By default two values are passed to the template:</p>\n\n<ul>\n<li><code>{url}</code> &ndash; The URL of the resource to be embedded.</li>\n<li><code>{callback}</code> &ndash; The JSONP callback to be executed.</li>\n</ul>\n\n\n<p>Example value:</p>\n\n<pre><code>widgetDefinition.providerUrl = new <a href=\"#!/api/CKEDITOR.template\" rel=\"CKEDITOR.template\" class=\"docClass\">CKEDITOR.template</a>(\n    '//ckeditor.iframe.ly/api/oembed?url={url}&amp;callback={callback}'\n);\n</code></pre>\n</div></div></div><div id='property-requiredContent' class='member  inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><a href='#!/api/CKEDITOR.feature' rel='CKEDITOR.feature' class='defined-in docClass'>CKEDITOR.feature</a><br/><a href='source/filter.html#CKEDITOR-feature-property-requiredContent' target='_blank' class='view-source'>view source</a></div><a href='#!/api/CKEDITOR.feature-property-requiredContent' class='name expandable'>requiredContent</a> : <a href=\"#!/api/CKEDITOR.filter.contentRule\" rel=\"CKEDITOR.filter.contentRule\" class=\"docClass\">CKEDITOR.filter.contentRule</a><span class=\"signature\"></span></div><div class='description'><div class='short'>Minimal HTML code that this feature must be allowed to\ngenerate in order to work. ...</div><div class='long'><p>Minimal HTML code that this feature must be allowed to\ngenerate in order to work.</p>\n\n<p>For example a basic image feature (image button displaying the image dialog window)\nneeds <code>'img[src,alt]'</code> in order to be activated.</p>\n\n<p>During the feature validation this value is passed to <a href=\"#!/api/CKEDITOR.filter-method-check\" rel=\"CKEDITOR.filter-method-check\" class=\"docClass\">CKEDITOR.filter.check</a>.</p>\n\n<p>If this value is not provided, a feature will be always activated.</p>\n<p>Defaults to: <code>null</code></p></div></div></div><div id='property-styleToAllowedContentRules' class='member  inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><a href='#!/api/CKEDITOR.plugins.widget.definition' rel='CKEDITOR.plugins.widget.definition' class='defined-in docClass'>CKEDITOR.plugins.widget.definition</a><br/><a href='source/plugin103.html#CKEDITOR-plugins-widget-definition-property-styleToAllowedContentRules' target='_blank' class='view-source'>view source</a></div><a href='#!/api/CKEDITOR.plugins.widget.definition-property-styleToAllowedContentRules' class='name expandable'>styleToAllowedContentRules</a> : Function<span class=\"signature\"></span></div><div class='description'><div class='short'>Function transforming custom widget's CKEDITOR.style instance into\nCKEDITOR.filter.allowedContentRules. ...</div><div class='long'><p>Function transforming custom widget's <a href=\"#!/api/CKEDITOR.style\" rel=\"CKEDITOR.style\" class=\"docClass\">CKEDITOR.style</a> instance into\n<a href=\"#!/api/CKEDITOR.filter.allowedContentRules\" rel=\"CKEDITOR.filter.allowedContentRules\" class=\"docClass\">CKEDITOR.filter.allowedContentRules</a>. It may be used when a static\n<a href=\"#!/api/CKEDITOR.plugins.widget.definition-property-styleableElements\" rel=\"CKEDITOR.plugins.widget.definition-property-styleableElements\" class=\"docClass\">styleableElements</a> property is not enough to inform the <a href=\"#!/api/CKEDITOR.filter\" rel=\"CKEDITOR.filter\" class=\"docClass\">CKEDITOR.filter</a>\nwhat HTML features should be enabled when allowing the given style.</p>\n\n<p>In most cases, when style's classes just have to be added to element name(s) used by\nthe widget element, it is recommended to use simpler <a href=\"#!/api/CKEDITOR.plugins.widget.definition-property-styleableElements\" rel=\"CKEDITOR.plugins.widget.definition-property-styleableElements\" class=\"docClass\">styleableElements</a> property.</p>\n\n<p>In order to get parsed classes from the style definition you can use\n<a href=\"#!/api/CKEDITOR.style.customHandlers.widget-method-getClassesArray\" rel=\"CKEDITOR.style.customHandlers.widget-method-getClassesArray\" class=\"docClass\">CKEDITOR.style.customHandlers.widget.getClassesArray</a>.</p>\n\n<p>For example, if you want to use the <a href=\"#!/guide/dev_allowed_content_rules-section-object-format\">object format of allowed content rules</a>,\nto specify <code>match</code> validator, your implementation could look like this:</p>\n\n<pre><code>editor.widgets.add( 'customWidget', {\n    // ...\n\n    styleToAllowedContentRules: funciton( style ) {\n        // Retrieve classes defined in the style.\n        var classes = style.getClassesArray();\n\n        // Do something crazy - for example return allowed content rules in object format,\n        // with custom match property and propertiesOnly flag.\n        return {\n            h1: {\n                match: isWidgetElement,\n                propertiesOnly: true,\n                classes: classes\n            }\n        };\n    }\n} );\n</code></pre>\n        <p>Available since: <b>4.4</b></p>\n<h3 class=\"pa\">Parameters</h3><ul><li><span class='pre'>style</span> : <a href=\"#!/api/CKEDITOR.style.customHandlers.widget\" rel=\"CKEDITOR.style.customHandlers.widget\" class=\"docClass\">CKEDITOR.style.customHandlers.widget</a><div class='sub-desc'><p>The style to be transformed.</p>\n</div></li></ul><h3 class='pa'>Returns</h3><ul><li><span class='pre'><a href=\"#!/api/CKEDITOR.filter.allowedContentRules\" rel=\"CKEDITOR.filter.allowedContentRules\" class=\"docClass\">CKEDITOR.filter.allowedContentRules</a></span><div class='sub-desc'>\n</div></li></ul></div></div></div><div id='property-styleableElements' class='member  inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><a href='#!/api/CKEDITOR.plugins.widget.definition' rel='CKEDITOR.plugins.widget.definition' class='defined-in docClass'>CKEDITOR.plugins.widget.definition</a><br/><a href='source/plugin103.html#CKEDITOR-plugins-widget-definition-property-styleableElements' target='_blank' class='view-source'>view source</a></div><a href='#!/api/CKEDITOR.plugins.widget.definition-property-styleableElements' class='name expandable'>styleableElements</a> : String<span class=\"signature\"></span></div><div class='description'><div class='short'>Names of element(s) (separated by spaces) for which the CKEDITOR.filter should allow classes\ndefined in the widget st...</div><div class='long'><p>Names of element(s) (separated by spaces) for which the <a href=\"#!/api/CKEDITOR.filter\" rel=\"CKEDITOR.filter\" class=\"docClass\">CKEDITOR.filter</a> should allow classes\ndefined in the widget styles. For example if your widget is upcasted from a simple <code>&lt;div&gt;</code>\nelement, then in order to make it styleable you can set:</p>\n\n<pre><code>editor.widgets.add( 'customWidget', {\n    upcast: function( element ) {\n        return element.name == 'div';\n    },\n\n    // ...\n\n    styleableElements: 'div'\n} );\n</code></pre>\n\n<p>Then, when the following style is defined:</p>\n\n<pre><code>{\n    name: 'Thick border', type: 'widget', widget: 'customWidget',\n    attributes: { 'class': 'thickBorder' }\n}\n</code></pre>\n\n<p>a rule allowing the <code>thickBorder</code> class for <code>div</code> elements will be registered in the <a href=\"#!/api/CKEDITOR.filter\" rel=\"CKEDITOR.filter\" class=\"docClass\">CKEDITOR.filter</a>.</p>\n\n<p>If you need to have more freedom when transforming widget style to allowed content rules,\nyou can use the <a href=\"#!/api/CKEDITOR.plugins.widget.definition-property-styleToAllowedContentRules\" rel=\"CKEDITOR.plugins.widget.definition-property-styleToAllowedContentRules\" class=\"docClass\">styleToAllowedContentRules</a> callback.</p>\n        <p>Available since: <b>4.4</b></p>\n</div></div></div><div id='property-template' class='member  inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><a href='#!/api/CKEDITOR.plugins.widget.definition' rel='CKEDITOR.plugins.widget.definition' class='defined-in docClass'>CKEDITOR.plugins.widget.definition</a><br/><a href='source/plugin103.html#CKEDITOR-plugins-widget-definition-property-template' target='_blank' class='view-source'>view source</a></div><a href='#!/api/CKEDITOR.plugins.widget.definition-property-template' class='name expandable'>template</a> : String<span class=\"signature\"></span></div><div class='description'><div class='short'>The template which will be used to create a new widget element (when the widget's command is executed). ...</div><div class='long'><p>The template which will be used to create a new widget element (when the widget's command is executed).\nThis string is populated with <a href=\"#!/api/CKEDITOR.plugins.widget.definition-property-defaults\" rel=\"CKEDITOR.plugins.widget.definition-property-defaults\" class=\"docClass\">default values</a> by using the <a href=\"#!/api/CKEDITOR.template\" rel=\"CKEDITOR.template\" class=\"docClass\">CKEDITOR.template</a> format.\nTherefore it has to be a valid <a href=\"#!/api/CKEDITOR.template\" rel=\"CKEDITOR.template\" class=\"docClass\">CKEDITOR.template</a> argument.</p>\n</div></div></div><div id='property-upcast' class='member  inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><a href='#!/api/CKEDITOR.plugins.widget.definition' rel='CKEDITOR.plugins.widget.definition' class='defined-in docClass'>CKEDITOR.plugins.widget.definition</a><br/><a href='source/plugin103.html#CKEDITOR-plugins-widget-definition-property-upcast' target='_blank' class='view-source'>view source</a></div><a href='#!/api/CKEDITOR.plugins.widget.definition-property-upcast' class='name expandable'>upcast</a> : String/Function<span class=\"signature\"></span></div><div class='description'><div class='short'>The function to be used to upcast an element to this widget or a\ncomma-separated list of upcast methods from the upca...</div><div class='long'><p>The function to be used to upcast an element to this widget or a\ncomma-separated list of upcast methods from the <a href=\"#!/api/CKEDITOR.plugins.widget.definition-property-upcasts\" rel=\"CKEDITOR.plugins.widget.definition-property-upcasts\" class=\"docClass\">upcasts</a> object.</p>\n\n<p>The upcast function <strong>is not</strong> executed in the widget context (because the widget\ndoes not exist yet) and two arguments are passed:</p>\n\n<ul>\n<li><code>element</code> (<a href=\"#!/api/CKEDITOR.htmlParser.element\" rel=\"CKEDITOR.htmlParser.element\" class=\"docClass\">CKEDITOR.htmlParser.element</a>) &ndash; The element to be checked.</li>\n<li><code>data</code> (<code>Object</code>) &ndash; The object which can be extended with data which will then be passed to the widget.</li>\n</ul>\n\n\n<p>An element will be upcasted if a function returned <code>true</code> or an instance of\na <a href=\"#!/api/CKEDITOR.htmlParser.element\" rel=\"CKEDITOR.htmlParser.element\" class=\"docClass\">CKEDITOR.htmlParser.element</a> if upcasting meant DOM structure changes\n(in this case the widget will be initialized on the returned element).</p>\n</div></div></div><div id='property-upcastPriority' class='member  inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><a href='#!/api/CKEDITOR.plugins.widget.definition' rel='CKEDITOR.plugins.widget.definition' class='defined-in docClass'>CKEDITOR.plugins.widget.definition</a><br/><a href='source/plugin103.html#CKEDITOR-plugins-widget-definition-property-upcastPriority' target='_blank' class='view-source'>view source</a></div><a href='#!/api/CKEDITOR.plugins.widget.definition-property-upcastPriority' class='name expandable'>upcastPriority</a> : Number<span class=\"signature\"></span></div><div class='description'><div class='short'>The upcast method(s) priority. ...</div><div class='long'><p>The <a href=\"#!/api/CKEDITOR.plugins.widget.definition-property-upcast\" rel=\"CKEDITOR.plugins.widget.definition-property-upcast\" class=\"docClass\">upcast</a> method(s) priority. The upcast with a lower priority number will be called before\nthe one with a higher number. The default priority is <code>10</code>.</p>\n<p>Defaults to: <code>10</code></p>        <p>Available since: <b>4.5</b></p>\n</div></div></div><div id='property-upcasts' class='member  inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><a href='#!/api/CKEDITOR.plugins.widget.definition' rel='CKEDITOR.plugins.widget.definition' class='defined-in docClass'>CKEDITOR.plugins.widget.definition</a><br/><a href='source/plugin103.html#CKEDITOR-plugins-widget-definition-property-upcasts' target='_blank' class='view-source'>view source</a></div><a href='#!/api/CKEDITOR.plugins.widget.definition-property-upcasts' class='name expandable'>upcasts</a> : Object<span class=\"signature\"></span></div><div class='description'><div class='short'>The object containing functions which can be used to upcast this widget. ...</div><div class='long'><p>The object containing functions which can be used to upcast this widget.\nOnly those pointed by the <a href=\"#!/api/CKEDITOR.plugins.widget.definition-property-upcast\" rel=\"CKEDITOR.plugins.widget.definition-property-upcast\" class=\"docClass\">upcast</a> property will be used.</p>\n\n<p>In most cases it is appropriate to use <a href=\"#!/api/CKEDITOR.plugins.widget.definition-property-upcast\" rel=\"CKEDITOR.plugins.widget.definition-property-upcast\" class=\"docClass\">upcast</a> directly,\nbecause majority of widgets need just one method.\nHowever, in some cases the widget author may want to expose more than one variant\nand then this property may be used.</p>\n\n<pre><code>upcasts: {\n    // This function may upcast only figure elements.\n    figure: function() {\n        // ...\n    },\n    // This function may upcast only image elements.\n    image: function() {\n        // ...\n    },\n    // More variants...\n}\n\n// Then, widget user may choose which upcast methods will be enabled.\neditor.on( 'widgetDefinition', function( evt ) {\n    if ( evt.data.name == 'image' )\n            evt.data.upcast = 'figure,image'; // Use both methods.\n} );\n</code></pre>\n</div></div></div><div id='property-urlRegExp' class='member  not-inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><span class='defined-in' rel='CKEDITOR.plugins.embedBase.baseDefinition'>CKEDITOR.plugins.embedBase.baseDefinition</span><br/><a href='source/plugin30.html#CKEDITOR-plugins-embedBase-baseDefinition-property-urlRegExp' target='_blank' class='view-source'>view source</a></div><a href='#!/api/CKEDITOR.plugins.embedBase.baseDefinition-property-urlRegExp' class='name expandable'>urlRegExp</a> : RegExp<span class=\"signature\"></span></div><div class='description'><div class='short'>A regular expression to pre-validate URLs. ...</div><div class='long'><p>A regular expression to pre-validate URLs.</p>\n\n<p>See:</p>\n\n<ul>\n<li>[https://iframely.com/docs/providers],</li>\n<li><a href=\"#!/api/CKEDITOR.plugins.embedBase.baseDefinition-method-isUrlValid\" rel=\"CKEDITOR.plugins.embedBase.baseDefinition-method-isUrlValid\" class=\"docClass\">isUrlValid</a>.</li>\n</ul>\n\n<p>Defaults to: <code>/^((https?:)?\\/\\/|www\\.)/i</code></p></div></div></div></div></div><div class='members-section'><div class='definedBy'>Defined By</div><h3 class='members-title icon-method'>Methods</h3><div class='subsection'><div id='method-_cacheResponse' class='member first-child not-inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><span class='defined-in' rel='CKEDITOR.plugins.embedBase.baseDefinition'>CKEDITOR.plugins.embedBase.baseDefinition</span><br/><a href='source/plugin30.html#CKEDITOR-plugins-embedBase-baseDefinition-method-_cacheResponse' target='_blank' class='view-source'>view source</a></div><a href='#!/api/CKEDITOR.plugins.embedBase.baseDefinition-method-_cacheResponse' class='name expandable'>_cacheResponse</a>( <span class='pre'>url, response</span> )<span class=\"signature\"><span class='private' >private</span></span></div><div class='description'><div class='short'>Caches the provider response. ...</div><div class='long'><p>Caches the provider response.</p>\n<h3 class=\"pa\">Parameters</h3><ul><li><span class='pre'>url</span> : String<div class='sub-desc'>\n</div></li><li><span class='pre'>response</span> : Object<div class='sub-desc'>\n</div></li></ul></div></div></div><div id='method-_createTask' class='member  not-inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><span class='defined-in' rel='CKEDITOR.plugins.embedBase.baseDefinition'>CKEDITOR.plugins.embedBase.baseDefinition</span><br/><a href='source/plugin30.html#CKEDITOR-plugins-embedBase-baseDefinition-method-_createTask' target='_blank' class='view-source'>view source</a></div><a href='#!/api/CKEDITOR.plugins.embedBase.baseDefinition-method-_createTask' class='name expandable'>_createTask</a>( <span class='pre'></span> ) : <a href=\"#!/api/CKEDITOR.plugins.notificationAggregator.task\" rel=\"CKEDITOR.plugins.notificationAggregator.task\" class=\"docClass\">CKEDITOR.plugins.notificationAggregator.task</a><span class=\"signature\"><span class='private' >private</span></span></div><div class='description'><div class='short'>Creates a notification aggregator task. ...</div><div class='long'><p>Creates a notification aggregator task.</p>\n<h3 class='pa'>Returns</h3><ul><li><span class='pre'><a href=\"#!/api/CKEDITOR.plugins.notificationAggregator.task\" rel=\"CKEDITOR.plugins.notificationAggregator.task\" class=\"docClass\">CKEDITOR.plugins.notificationAggregator.task</a></span><div class='sub-desc'>\n</div></li></ul></div></div></div><div id='method-_getCachedResponse' class='member  not-inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><span class='defined-in' rel='CKEDITOR.plugins.embedBase.baseDefinition'>CKEDITOR.plugins.embedBase.baseDefinition</span><br/><a href='source/plugin30.html#CKEDITOR-plugins-embedBase-baseDefinition-method-_getCachedResponse' target='_blank' class='view-source'>view source</a></div><a href='#!/api/CKEDITOR.plugins.embedBase.baseDefinition-method-_getCachedResponse' class='name expandable'>_getCachedResponse</a>( <span class='pre'>url</span> ) : Object/undefined<span class=\"signature\"><span class='private' >private</span></span></div><div class='description'><div class='short'>Returns the cached response. ...</div><div class='long'><p>Returns the cached response.</p>\n<h3 class=\"pa\">Parameters</h3><ul><li><span class='pre'>url</span> : String<div class='sub-desc'>\n</div></li></ul><h3 class='pa'>Returns</h3><ul><li><span class='pre'>Object/undefined</span><div class='sub-desc'><p>Response or <code>undefined</code> if the cache was missed.</p>\n</div></li></ul></div></div></div><div id='method-_handleError' class='member  not-inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><span class='defined-in' rel='CKEDITOR.plugins.embedBase.baseDefinition'>CKEDITOR.plugins.embedBase.baseDefinition</span><br/><a href='source/plugin30.html#CKEDITOR-plugins-embedBase-baseDefinition-method-_handleError' target='_blank' class='view-source'>view source</a></div><a href='#!/api/CKEDITOR.plugins.embedBase.baseDefinition-method-_handleError' class='name expandable'>_handleError</a>( <span class='pre'>request, messageTypeOrMessage</span> )<span class=\"signature\"><span class='private' >private</span></span></div><div class='description'><div class='short'>Handles an error. ...</div><div class='long'><p>Handles an error. An error can be caused either by a request failure or an unsupported\noEmbed response type.</p>\n<h3 class=\"pa\">Parameters</h3><ul><li><span class='pre'>request</span> : <a href=\"#!/api/CKEDITOR.plugins.embedBase.request\" rel=\"CKEDITOR.plugins.embedBase.request\" class=\"docClass\">CKEDITOR.plugins.embedBase.request</a><div class='sub-desc'>\n</div></li><li><span class='pre'>messageTypeOrMessage</span> : String<div class='sub-desc'><p>See <a href=\"#!/api/CKEDITOR.plugins.embedBase.baseDefinition-method-getErrorMessage\" rel=\"CKEDITOR.plugins.embedBase.baseDefinition-method-getErrorMessage\" class=\"docClass\">getErrorMessage</a>.</p>\n</div></li></ul></div></div></div><div id='method-_handleResponse' class='member  not-inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><span class='defined-in' rel='CKEDITOR.plugins.embedBase.baseDefinition'>CKEDITOR.plugins.embedBase.baseDefinition</span><br/><a href='source/plugin30.html#CKEDITOR-plugins-embedBase-baseDefinition-method-_handleResponse' target='_blank' class='view-source'>view source</a></div><a href='#!/api/CKEDITOR.plugins.embedBase.baseDefinition-method-_handleResponse' class='name expandable'>_handleResponse</a>( <span class='pre'>request</span> ) : Boolean<span class=\"signature\"><span class='private' >private</span></span></div><div class='description'><div class='short'>Handles the response of a successful request. ...</div><div class='long'><p>Handles the response of a successful request.</p>\n\n<p>Fires the <a href=\"#!/api/CKEDITOR.plugins.embedBase.baseDefinition-event-handleResponse\" rel=\"CKEDITOR.plugins.embedBase.baseDefinition-event-handleResponse\" class=\"docClass\">handleResponse</a> event in order to convert the oEmbed response\nto HTML that can be embedded.</p>\n\n<p>If the response can be handled, the <a href=\"#!/api/CKEDITOR.plugins.embedBase.baseDefinition-method-_setContent\" rel=\"CKEDITOR.plugins.embedBase.baseDefinition-method-_setContent\" class=\"docClass\">content is set</a>.</p>\n<h3 class=\"pa\">Parameters</h3><ul><li><span class='pre'>request</span> : <a href=\"#!/api/CKEDITOR.plugins.embedBase.request\" rel=\"CKEDITOR.plugins.embedBase.request\" class=\"docClass\">CKEDITOR.plugins.embedBase.request</a><div class='sub-desc'>\n</div></li></ul><h3 class='pa'>Returns</h3><ul><li><span class='pre'>Boolean</span><div class='sub-desc'><p>Whether the response can be handled. Returns <code>false</code> if <a href=\"#!/api/CKEDITOR.plugins.embedBase.baseDefinition-event-handleResponse\" rel=\"CKEDITOR.plugins.embedBase.baseDefinition-event-handleResponse\" class=\"docClass\">handleResponse</a>\nwas canceled or the default listener could not convert oEmbed response into embeddable HTML.</p>\n</div></li></ul></div></div></div><div id='method-_responseToHtml' class='member  not-inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><span class='defined-in' rel='CKEDITOR.plugins.embedBase.baseDefinition'>CKEDITOR.plugins.embedBase.baseDefinition</span><br/><a href='source/plugin30.html#CKEDITOR-plugins-embedBase-baseDefinition-method-_responseToHtml' target='_blank' class='view-source'>view source</a></div><a href='#!/api/CKEDITOR.plugins.embedBase.baseDefinition-method-_responseToHtml' class='name expandable'>_responseToHtml</a>( <span class='pre'>response</span> ) : String/null<span class=\"signature\"><span class='private' >private</span></span></div><div class='description'><div class='short'>Returns embeddable HTML for an oEmbed response if it is of the photo, video or rich type. ...</div><div class='long'><p>Returns embeddable HTML for an oEmbed response if it is of the <code>photo</code>, <code>video</code> or <code>rich</code> type.</p>\n<h3 class=\"pa\">Parameters</h3><ul><li><span class='pre'>response</span> : Object<div class='sub-desc'><p>The oEmbed response.</p>\n</div></li></ul><h3 class='pa'>Returns</h3><ul><li><span class='pre'>String/null</span><div class='sub-desc'><p>HTML string to be embedded or <code>null</code> if this response type is not supported.</p>\n</div></li></ul></div></div></div><div id='method-_sendRequest' class='member  not-inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><span class='defined-in' rel='CKEDITOR.plugins.embedBase.baseDefinition'>CKEDITOR.plugins.embedBase.baseDefinition</span><br/><a href='source/plugin30.html#CKEDITOR-plugins-embedBase-baseDefinition-method-_sendRequest' target='_blank' class='view-source'>view source</a></div><a href='#!/api/CKEDITOR.plugins.embedBase.baseDefinition-method-_sendRequest' class='name expandable'>_sendRequest</a>( <span class='pre'>request</span> )<span class=\"signature\"><span class='private' >private</span></span></div><div class='description'><div class='short'>Sends the request to the provider using\nthe JSONP technique. ...</div><div class='long'><p>Sends the request to the <a href=\"#!/api/CKEDITOR.plugins.embedBase.baseDefinition-property-providerUrl\" rel=\"CKEDITOR.plugins.embedBase.baseDefinition-property-providerUrl\" class=\"docClass\">provider</a> using\nthe <a href=\"#!/api/CKEDITOR.plugins.embedBase._jsonp\" rel=\"CKEDITOR.plugins.embedBase._jsonp\" class=\"docClass\">JSONP</a> technique.</p>\n<h3 class=\"pa\">Parameters</h3><ul><li><span class='pre'>request</span> : <a href=\"#!/api/CKEDITOR.plugins.embedBase.request\" rel=\"CKEDITOR.plugins.embedBase.request\" class=\"docClass\">CKEDITOR.plugins.embedBase.request</a><div class='sub-desc'>\n</div></li></ul></div></div></div><div id='method-_setContent' class='member  not-inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><span class='defined-in' rel='CKEDITOR.plugins.embedBase.baseDefinition'>CKEDITOR.plugins.embedBase.baseDefinition</span><br/><a href='source/plugin30.html#CKEDITOR-plugins-embedBase-baseDefinition-method-_setContent' target='_blank' class='view-source'>view source</a></div><a href='#!/api/CKEDITOR.plugins.embedBase.baseDefinition-method-_setContent' class='name expandable'>_setContent</a>( <span class='pre'>url, content</span> )<span class=\"signature\"><span class='private' >private</span></span></div><div class='description'><div class='short'>The very final step of content loading. ...</div><div class='long'><p>The very final step of <a href=\"#!/api/CKEDITOR.plugins.embedBase.baseDefinition-method-loadContent\" rel=\"CKEDITOR.plugins.embedBase.baseDefinition-method-loadContent\" class=\"docClass\">content loading</a>. The <code>url</code> data property is changed\nand the content is embedded (<a href=\"#!/api/CKEDITOR.plugins.widget-property-element\" rel=\"CKEDITOR.plugins.widget-property-element\" class=\"docClass\">CKEDITOR.plugins.widget.element</a>'s HTML is set).</p>\n<h3 class=\"pa\">Parameters</h3><ul><li><span class='pre'>url</span> : String<div class='sub-desc'><p>The resource URL.</p>\n</div></li><li><span class='pre'>content</span> : String<div class='sub-desc'><p>HTML content to be embedded.</p>\n</div></li></ul></div></div></div><div id='method-getErrorMessage' class='member  not-inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><span class='defined-in' rel='CKEDITOR.plugins.embedBase.baseDefinition'>CKEDITOR.plugins.embedBase.baseDefinition</span><br/><a href='source/plugin30.html#CKEDITOR-plugins-embedBase-baseDefinition-method-getErrorMessage' target='_blank' class='view-source'>view source</a></div><a href='#!/api/CKEDITOR.plugins.embedBase.baseDefinition-method-getErrorMessage' class='name expandable'>getErrorMessage</a>( <span class='pre'>messageTypeOrMessage, [url], [suffix]</span> ) : String<span class=\"signature\"></span></div><div class='description'><div class='short'>Generates an error message based on the message type (with a possible suffix) or\nthe custom message template. ...</div><div class='long'><p>Generates an error message based on the message type (with a possible suffix) or\nthe custom message template.</p>\n\n<p>This method is used when showing a notification or an alert (in a dialog) about an error.\nUsually it is used with an error type which is a string from the <code>editor.lang.embedbase</code> object.</p>\n\n<p>There are two error types available at the moment: <code>'unsupportedUrl'</code> and <code>'fetchingFailed'</code>.\nAdditionally, both can be suffixed with <code>'Given'</code>. See the language entries to see the difference.\nInside the dialog this method is used with a suffix and to generate a notification message it is\nused without a suffix.</p>\n\n<p>Additionally, a custom message may be passed and just like language entries, it can use the <code>{url}</code>\nplaceholder.</p>\n\n<p>While <a href=\"#!/api/CKEDITOR.plugins.embedBase.baseDefinition-event-handleResponse\" rel=\"CKEDITOR.plugins.embedBase.baseDefinition-event-handleResponse\" class=\"docClass\">handling the response</a> you can set an error message or its type. It will\nbe passed to this method later.</p>\n\n<pre><code>widget.on( 'handleResponse', function( evt ) {\n    if ( evt.data.response.type != 'rich' ) {\n        evt.data.errorMessage = '{url} cannot be embedded. Only rich type is supported.';\n        evt.cancel();\n\n        // Or:\n        evt.data.errorMessage = 'unsupportedUrl.';\n        evt.cancel();\n    }\n} );\n</code></pre>\n\n<p>If you need to display your own error:</p>\n\n<pre><code>editor.showNotification(\n    widget.getErrorMessage( '{url} cannot be embedded. Only rich type is supported.', wrongUrl )\n);\n</code></pre>\n\n<p>Or with a message type:</p>\n\n<pre><code>editor.showNotification(\n    widget.getErrorMessage( 'unsupportedUrl', wrongUrl )\n);\n</code></pre>\n<h3 class=\"pa\">Parameters</h3><ul><li><span class='pre'>messageTypeOrMessage</span> : String<div class='sub-desc'>\n</div></li><li><span class='pre'>url</span> : String (optional)<div class='sub-desc'>\n</div></li><li><span class='pre'>suffix</span> : String (optional)<div class='sub-desc'>\n</div></li></ul><h3 class='pa'>Returns</h3><ul><li><span class='pre'>String</span><div class='sub-desc'>\n</div></li></ul></div></div></div><div id='method-isUrlValid' class='member  not-inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><span class='defined-in' rel='CKEDITOR.plugins.embedBase.baseDefinition'>CKEDITOR.plugins.embedBase.baseDefinition</span><br/><a href='source/plugin30.html#CKEDITOR-plugins-embedBase-baseDefinition-method-isUrlValid' target='_blank' class='view-source'>view source</a></div><a href='#!/api/CKEDITOR.plugins.embedBase.baseDefinition-method-isUrlValid' class='name expandable'>isUrlValid</a>( <span class='pre'>url</span> ) : Boolean<span class=\"signature\"></span></div><div class='description'><div class='short'>Checks whether the URL is valid. ...</div><div class='long'><p>Checks whether the URL is valid. Usually the content provider makes the final validation\nas only the provider knows what kind of URLs are accepted. However, to give the user some immediate feedback\na synchronous validation is performed using the <a href=\"#!/api/CKEDITOR.plugins.embedBase.baseDefinition-property-urlRegExp\" rel=\"CKEDITOR.plugins.embedBase.baseDefinition-property-urlRegExp\" class=\"docClass\">urlRegExp</a> pattern and the <a href=\"#!/api/CKEDITOR.plugins.embedBase.baseDefinition-event-validateUrl\" rel=\"CKEDITOR.plugins.embedBase.baseDefinition-event-validateUrl\" class=\"docClass\">validateUrl</a> event.</p>\n<h3 class=\"pa\">Parameters</h3><ul><li><span class='pre'>url</span> : String<div class='sub-desc'><p>The URL to check.</p>\n</div></li></ul><h3 class='pa'>Returns</h3><ul><li><span class='pre'>Boolean</span><div class='sub-desc'><p>Whether the URL is valid (supported).</p>\n</div></li></ul></div></div></div><div id='method-loadContent' class='member  not-inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><span class='defined-in' rel='CKEDITOR.plugins.embedBase.baseDefinition'>CKEDITOR.plugins.embedBase.baseDefinition</span><br/><a href='source/plugin30.html#CKEDITOR-plugins-embedBase-baseDefinition-method-loadContent' target='_blank' class='view-source'>view source</a></div><a href='#!/api/CKEDITOR.plugins.embedBase.baseDefinition-method-loadContent' class='name expandable'>loadContent</a>( <span class='pre'>url, opts</span> ) : <a href=\"#!/api/CKEDITOR.plugins.embedBase.request\" rel=\"CKEDITOR.plugins.embedBase.request\" class=\"docClass\">CKEDITOR.plugins.embedBase.request</a><span class=\"signature\"></span></div><div class='description'><div class='short'>Loads content for a given resource URL by requesting the provider. ...</div><div class='long'><p>Loads content for a given resource URL by requesting the <a href=\"#!/api/CKEDITOR.plugins.embedBase.baseDefinition-property-providerUrl\" rel=\"CKEDITOR.plugins.embedBase.baseDefinition-property-providerUrl\" class=\"docClass\">provider</a>.</p>\n\n<p>Usually widgets are controlled by the <a href=\"#!/api/CKEDITOR.plugins.widget-method-setData\" rel=\"CKEDITOR.plugins.widget-method-setData\" class=\"docClass\">CKEDITOR.plugins.widget.setData</a> method. However,\nloading content is an asynchronous operation due to client-server communication, and it would not\nbe possible to pass callbacks to the <a href=\"#!/api/CKEDITOR.plugins.widget-method-setData\" rel=\"CKEDITOR.plugins.widget-method-setData\" class=\"docClass\">CKEDITOR.plugins.widget.setData</a> method so this new method\nis defined for embed widgets.</p>\n\n<p>This method fires two events that allow to customize widget behavior without changing its code:</p>\n\n<ul>\n<li><a href=\"#!/api/CKEDITOR.plugins.embedBase.baseDefinition-event-sendRequest\" rel=\"CKEDITOR.plugins.embedBase.baseDefinition-event-sendRequest\" class=\"docClass\">sendRequest</a>,</li>\n<li><a href=\"#!/api/CKEDITOR.plugins.embedBase.baseDefinition-event-handleResponse\" rel=\"CKEDITOR.plugins.embedBase.baseDefinition-event-handleResponse\" class=\"docClass\">handleResponse</a> (if the request was successful).</li>\n</ul>\n\n\n<p>Note: This method is always asynchronous, even if the cache was hit.</p>\n\n<p>Example usage:</p>\n\n<pre><code>var url = 'https://twitter.com/reinmarpl/status/573118615274315776';\nwidget.loadContent( url, {\n    callback: function() {\n        // Success. It is a good time to save a snapshot.\n        editor.fire( 'saveSnapshot' );\n        console.log( widget.data.url ); // The above URL. It is only changed\n                                        // once the content is successfully loaded.\n    },\n\n    errorCallback: function( message ) {\n        editor.showNotification( widget.getErrorMessage( message, url ), 'warning' );\n    }\n} );\n</code></pre>\n<h3 class=\"pa\">Parameters</h3><ul><li><span class='pre'>url</span> : String<div class='sub-desc'><p>Resource URL to be embedded.</p>\n</div></li><li><span class='pre'>opts</span> : Object<div class='sub-desc'>\n<ul><li><span class='pre'>callback</span> : Function (optional)<div class='sub-desc'><p>Callback called when content was successfully loaded into the editor.</p>\n</div></li><li><span class='pre'>errorCallback</span> : Function (optional)<div class='sub-desc'><p>Callback called when an error occurred.</p>\n<h3 class=\"pa\">Parameters</h3><ul><li><span class='pre'>messageTypeOrMessage</span> : String<div class='sub-desc'><p>See <a href=\"#!/api/CKEDITOR.plugins.embedBase.baseDefinition-method-getErrorMessage\" rel=\"CKEDITOR.plugins.embedBase.baseDefinition-method-getErrorMessage\" class=\"docClass\">getErrorMessage</a>.</p>\n</div></li></ul></div></li><li><span class='pre'>noNotifications</span> : Boolean (optional)<div class='sub-desc'><p>Do not show notifications (useful when the dialog is open).</p>\n</div></li></ul></div></li></ul><h3 class='pa'>Returns</h3><ul><li><span class='pre'><a href=\"#!/api/CKEDITOR.plugins.embedBase.request\" rel=\"CKEDITOR.plugins.embedBase.request\" class=\"docClass\">CKEDITOR.plugins.embedBase.request</a></span><div class='sub-desc'>\n</div></li></ul></div></div></div><div id='method-toFeature' class='member  inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><a href='#!/api/CKEDITOR.feature' rel='CKEDITOR.feature' class='defined-in docClass'>CKEDITOR.feature</a><br/><a href='source/filter.html#CKEDITOR-feature-method-toFeature' target='_blank' class='view-source'>view source</a></div><a href='#!/api/CKEDITOR.feature-method-toFeature' class='name expandable'>toFeature</a>( <span class='pre'></span> ) : <a href=\"#!/api/CKEDITOR.feature\" rel=\"CKEDITOR.feature\" class=\"docClass\">CKEDITOR.feature</a><span class=\"signature\"></span></div><div class='description'><div class='short'>Returns a feature that this feature needs to register. ...</div><div class='long'><p>Returns a feature that this feature needs to register.</p>\n\n<p>In some cases, during activation, one feature may need to register\nanother feature. For example a <a href=\"#!/api/CKEDITOR.ui.button\" rel=\"CKEDITOR.ui.button\" class=\"docClass\">CKEDITOR.ui.button</a> often registers\na related command. See <a href=\"#!/api/CKEDITOR.ui.button-method-toFeature\" rel=\"CKEDITOR.ui.button-method-toFeature\" class=\"docClass\">CKEDITOR.ui.button.toFeature</a>.</p>\n\n<p>This method is executed when a feature is passed to the <a href=\"#!/api/CKEDITOR.editor-method-addFeature\" rel=\"CKEDITOR.editor-method-addFeature\" class=\"docClass\">CKEDITOR.editor.addFeature</a>.</p>\n<h3 class='pa'>Returns</h3><ul><li><span class='pre'><a href=\"#!/api/CKEDITOR.feature\" rel=\"CKEDITOR.feature\" class=\"docClass\">CKEDITOR.feature</a></span><div class='sub-desc'>\n</div></li></ul></div></div></div></div></div><div class='members-section'><div class='definedBy'>Defined By</div><h3 class='members-title icon-event'>Events</h3><div class='subsection'><div id='event-handleResponse' class='member first-child not-inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><span class='defined-in' rel='CKEDITOR.plugins.embedBase.baseDefinition'>CKEDITOR.plugins.embedBase.baseDefinition</span><br/><a href='source/plugin30.html#CKEDITOR-plugins-embedBase-baseDefinition-event-handleResponse' target='_blank' class='view-source'>view source</a></div><a href='#!/api/CKEDITOR.plugins.embedBase.baseDefinition-event-handleResponse' class='name expandable'>handleResponse</a>( <span class='pre'>evt</span> )<span class=\"signature\"></span></div><div class='description'><div class='short'>Fired after receiving a response from the provider. ...</div><div class='long'><p>Fired after receiving a response from the <a href=\"#!/api/CKEDITOR.plugins.embedBase.baseDefinition-property-providerUrl\" rel=\"CKEDITOR.plugins.embedBase.baseDefinition-property-providerUrl\" class=\"docClass\">provider</a>.\nThis event listener job is to turn the oEmbed response to embeddable HTML by setting\n<code>evt.data.html</code>.</p>\n\n<pre><code>widget.on( 'handleReaponse', function( evt ) {\n    evt.data.html = customOembedToHtmlConverter( evt.data.response );\n} );\n</code></pre>\n\n<p>This event can also be canceled to indicate that the response cannot be handled. In such\ncase the <code>evt.data.errorMessage</code> must be set (see <a href=\"#!/api/CKEDITOR.plugins.embedBase.baseDefinition-method-getErrorMessage\" rel=\"CKEDITOR.plugins.embedBase.baseDefinition-method-getErrorMessage\" class=\"docClass\">getErrorMessage</a>).</p>\n\n<pre><code>widget.on( 'handleReaponse', function( evt ) {\n    if ( evt.data.response.type == 'photo' ) {\n        // Will display the editor.lang.embedbase.unsupportedUrl(Given) message.\n        evt.data.errorMessage = 'unsupportedUrl';\n        evt.cancel();\n    }\n} );\n</code></pre>\n\n<p>This event has a default late-listener (with a priority of <code>999</code>) that, if <code>evt.data.html</code> has not\nbeen set yet, will try to handle the response by using the <a href=\"#!/api/CKEDITOR.plugins.embedBase.baseDefinition-method-_responseToHtml\" rel=\"CKEDITOR.plugins.embedBase.baseDefinition-method-_responseToHtml\" class=\"docClass\">_responseToHtml</a> method.</p>\n<h3 class=\"pa\">Parameters</h3><ul><li><span class='pre'>evt</span> : <a href=\"#!/api/CKEDITOR.eventInfo\" rel=\"CKEDITOR.eventInfo\" class=\"docClass\">CKEDITOR.eventInfo</a><div class='sub-desc'>\n<ul><li><span class='pre'>data</span> : Object<div class='sub-desc'>\n<ul><li><span class='pre'>url</span> : String<div class='sub-desc'><p>The resource URL.</p>\n</div></li><li><span class='pre'>response</span> : Object<div class='sub-desc'><p>The oEmbed response.</p>\n</div></li><li><span class='pre'>html</span> : String (optional)<div class='sub-desc'><p>The HTML which will be embedded.</p>\n<p>Defaults to: <code>''</code></p></div></li><li><span class='pre'>errorMessage</span> : String (optional)<div class='sub-desc'><p>The error message or message type (see <a href=\"#!/api/CKEDITOR.plugins.embedBase.baseDefinition-method-getErrorMessage\" rel=\"CKEDITOR.plugins.embedBase.baseDefinition-method-getErrorMessage\" class=\"docClass\">getErrorMessage</a>)\nthat must be set if this event is canceled to indicate an unsupported oEmbed response.</p>\n</div></li></ul></div></li></ul></div></li></ul></div></div></div><div id='event-sendRequest' class='member  not-inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><span class='defined-in' rel='CKEDITOR.plugins.embedBase.baseDefinition'>CKEDITOR.plugins.embedBase.baseDefinition</span><br/><a href='source/plugin30.html#CKEDITOR-plugins-embedBase-baseDefinition-event-sendRequest' target='_blank' class='view-source'>view source</a></div><a href='#!/api/CKEDITOR.plugins.embedBase.baseDefinition-event-sendRequest' class='name expandable'>sendRequest</a>( <span class='pre'>evt</span> )<span class=\"signature\"></span></div><div class='description'><div class='short'>Fired by the loadContent method to dispatch a request to the provider. ...</div><div class='long'><p>Fired by the <a href=\"#!/api/CKEDITOR.plugins.embedBase.baseDefinition-method-loadContent\" rel=\"CKEDITOR.plugins.embedBase.baseDefinition-method-loadContent\" class=\"docClass\">loadContent</a> method to dispatch a request to the provider.\nYou can cancel this event and send the request using a different technique.\nBy default, if the event is not stopped or canceled a request will be sent\nusing the JSONP technique.</p>\n\n<pre><code>widget.on( 'sendRequest', function( evt ) {\n    var request = evt.data;\n\n    // Send the request using a technique of your choice (XHR with CORS for instance).\n    myApp.requestOembedProvider( request.url, function( err, response ) {\n        if ( err ) {\n            request.errorCallback( err );\n        } else {\n            request.callback( response );\n        }\n    } );\n\n    // Do not call other listeners, so the default behavior (JSONP request)\n    // will not be executed.\n    evt.stop();\n} );\n</code></pre>\n<h3 class=\"pa\">Parameters</h3><ul><li><span class='pre'>evt</span> : <a href=\"#!/api/CKEDITOR.eventInfo\" rel=\"CKEDITOR.eventInfo\" class=\"docClass\">CKEDITOR.eventInfo</a><div class='sub-desc'>\n<ul><li><span class='pre'>data</span> : <a href=\"#!/api/CKEDITOR.plugins.embedBase.request\" rel=\"CKEDITOR.plugins.embedBase.request\" class=\"docClass\">CKEDITOR.plugins.embedBase.request</a><div class='sub-desc'>\n</div></li></ul></div></li></ul></div></div></div><div id='event-validateUrl' class='member  not-inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><span class='defined-in' rel='CKEDITOR.plugins.embedBase.baseDefinition'>CKEDITOR.plugins.embedBase.baseDefinition</span><br/><a href='source/plugin30.html#CKEDITOR-plugins-embedBase-baseDefinition-event-validateUrl' target='_blank' class='view-source'>view source</a></div><a href='#!/api/CKEDITOR.plugins.embedBase.baseDefinition-event-validateUrl' class='name expandable'>validateUrl</a>( <span class='pre'>evt</span> )<span class=\"signature\"></span></div><div class='description'><div class='short'>Fired by the isUrlValid method. ...</div><div class='long'><p>Fired by the <a href=\"#!/api/CKEDITOR.plugins.embedBase.baseDefinition-method-isUrlValid\" rel=\"CKEDITOR.plugins.embedBase.baseDefinition-method-isUrlValid\" class=\"docClass\">isUrlValid</a> method. Cancel the event to make the URL invalid.</p>\n<h3 class=\"pa\">Parameters</h3><ul><li><span class='pre'>evt</span> : <a href=\"#!/api/CKEDITOR.eventInfo\" rel=\"CKEDITOR.eventInfo\" class=\"docClass\">CKEDITOR.eventInfo</a><div class='sub-desc'>\n<ul><li><span class='pre'>data</span> : String<div class='sub-desc'><p>The URL being validated.</p>\n</div></li></ul></div></li></ul></div></div></div></div></div></div></div>","meta":{"abstract":true}});